mapper.select:
/* selectScheduleCount */
SELECT COUNT(1)
FROM T_SC_SCH TSS
WHERE 1 = 1
  AND TSS.SCHM_SEQ = #{schedule.schmSeq}
  AND TSS.SCH_SEQ = #{schedule.schSeq}
  AND TSS.USE_YND = 'Y'

mapper.update:
/* updateSchedule */
UPDATE T_SC_SCH TSS
SET TSS.USE_YND     = 'N',
    TSS.MODIFY_DATE = NOW(),
    TSS.MODIFY_SEQ  = #{user.userSeq}
WHERE 1 = 1
  AND TSS.SCHM_SEQ = #{schedule.schmSeq}
  AND TSS.SCH_SEQ = #{schedule.schSeq}
  AND TSS.USE_YND = 'Y'

/* updateScheduleUsers */
UPDATE T_SC_SCH_USER TSSU
SET TSSU.USE_YND     = 'N',
    TSSU.MODIFY_DATE = NOW(),
    TSSU.MODIFY_SEQ  = #{user.userSeq}
WHERE 1 = 1
  AND TSSU.SCHM_SEQ = #{schedule.schmSeq}
  AND TSSU.SCH_SEQ = #{schedule.schSeq}
  AND TSSU.USE_YND = 'Y'
