/* selectSchedulesByAuthorizedCalendarSequencesAndDate */ SELECT IFNULL(TSS.SCHM_SEQ, '') AS schmSeq, IFNULL(TSS.SCH_SEQ, '') AS schSeq, IFNULL(TSS.SCH_TITLE, '') AS schTitle, IFNULL(TSS.SCH_CONTENT, '') AS schContent, DATE_FORMAT(TSS.START_DATE, '%Y%m%d%H%i') AS startDate, DATE_FORMAT(TSS.END_DATE, '%Y%m%d%H%i') AS endDate, IFNULL(TSS.ALL_DAY_YN, '') AS allDayYn, IFNULL(TSS.CAL_SEQ,'') AS calSeq, IFNULL(TSS.CREATE_SEQ, '') AS createSeq, DATE_FORMAT(TSS.CREATE_DATE, '%Y%m%d%H%i') AS createDate, IFNULL(TSS.MODIFY_SEQ, '') AS modifySeq, CASE WHEN TSS.MODIFY_DATE IS NOT NULL THEN DATE_FORMAT(TSS.MODIFY_DATE, '%Y%m%d%H%i') ELSE '' END AS modifyDate FROM T_SC_SCH TSS WHERE 1 = 1 AND TSS.SCHM_SEQ > 0 AND TSS.SCH_SEQ > 0 AND EXISTS(SELECT 1 FROM T_SC_SCH T_EXISTS_SCH WHERE 1 = 1 AND (T_EXISTS_SCH.START_DATE_YEAR = #{schedule.year} AND T_EXISTS_SCH.START_DATE_MONTH = #{schedule.month}) AND T_EXISTS_SCH.SCHM_SEQ = TSS.SCHM_SEQ AND T_EXISTS_SCH.SCH_SEQ = TSS.SCH_SEQ
UNION ALL
SELECT 1 FROM T_SC_SCH T_EXISTS_SCH WHERE 1 = 1 AND (T_EXISTS_SCH.END_DATE_YEAR = #{schedule.year} AND T_EXISTS_SCH.END_DATE_MONTH = #{schedule.month}) AND T_EXISTS_SCH.SCHM_SEQ = TSS.SCHM_SEQ AND T_EXISTS_SCH.SCH_SEQ = TSS.SCH_SEQ
UNION ALL
SELECT 1 FROM T_SC_SCH T_EXISTS_SCH WHERE 1 = 1 AND ( (T_EXISTS_SCH.START_DATE_YEAR <= #{schedule.year} AND T_EXISTS_SCH.START_DATE_MONTH < #{schedule.month}) AND (T_EXISTS_SCH.END_DATE_YEAR >= #{schedule.year} AND T_EXISTS_SCH.END_DATE_MONTH > #{schedule.month}) ) AND T_EXISTS_SCH.SCHM_SEQ = TSS.SCHM_SEQ AND T_EXISTS_SCH.SCH_SEQ = TSS.SCH_SEQ) AND EXISTS(SELECT 1 FROM T_SC_SCH_USER TSSU INNER JOIN T_SC_CAL TSC ON TSS.CAL_SEQ = TSC.CAL_SEQ AND TSC.CAL_SEQ IN #{calendar.authorizedCalendarSequence}
AND TSSU.USE_YND = 'Y' AND TSC.USE_YND = 'Y' WHERE 1 = 1 AND TSSU.SCHM_SEQ = TSS.SCHM_SEQ AND TSSU.SCH_SEQ = TSS.SCH_SEQ) AND TSS.USE_YND = 'Y'

/* selectSchedulesByUnAuthorizedCalendarSequencesAndDate */ SELECT IFNULL(TSS.SCHM_SEQ, '') AS schmSeq, IFNULL(TSS.SCH_SEQ, '') AS schSeq, IFNULL(TSS.SCH_TITLE, '') AS schTitle, IFNULL(TSS.SCH_CONTENT, '') AS schContent, DATE_FORMAT(TSS.START_DATE, '%Y%m%d%H%i') AS startDate, DATE_FORMAT(TSS.END_DATE, '%Y%m%d%H%i') AS endDate, IFNULL(TSS.ALL_DAY_YN, '') AS allDayYn, IFNULL(TSS.CAL_SEQ,'') AS calSeq, IFNULL(TSS.CREATE_SEQ, '') AS createSeq, DATE_FORMAT(TSS.CREATE_DATE, '%Y%m%d%H%i') AS createDate, IFNULL(TSS.MODIFY_SEQ, '') AS modifySeq, CASE WHEN TSS.MODIFY_DATE IS NOT NULL THEN DATE_FORMAT(TSS.MODIFY_DATE, '%Y%m%d%H%i') ELSE '' END AS modifyDate, IFNULL(TSS.CAL_SEQ,'') AS calSeq FROM T_SC_SCH TSS WHERE 1 = 1 AND TSS.SCHM_SEQ > 0 AND TSS.SCH_SEQ > 0 AND EXISTS(SELECT 1 FROM T_SC_SCH T_EXISTS_SCH WHERE 1 = 1 AND (T_EXISTS_SCH.START_DATE_YEAR = #{schedule.year} AND T_EXISTS_SCH.START_DATE_MONTH = #{schedule.month}) AND T_EXISTS_SCH.SCHM_SEQ = TSS.SCHM_SEQ AND T_EXISTS_SCH.SCH_SEQ = TSS.SCH_SEQ
UNION ALL
SELECT 1 FROM T_SC_SCH T_EXISTS_SCH WHERE 1 = 1 AND (T_EXISTS_SCH.END_DATE_YEAR = #{schedule.year} AND T_EXISTS_SCH.END_DATE_MONTH = #{schedule.month}) AND T_EXISTS_SCH.SCHM_SEQ = TSS.SCHM_SEQ AND T_EXISTS_SCH.SCH_SEQ = TSS.SCH_SEQ
UNION ALL
SELECT 1 FROM T_SC_SCH T_EXISTS_SCH WHERE 1 = 1 AND ( (T_EXISTS_SCH.START_DATE_YEAR <= #{schedule.year} AND T_EXISTS_SCH.START_DATE_MONTH < #{schedule.month}) AND (T_EXISTS_SCH.END_DATE_YEAR >= #{schedule.year} AND T_EXISTS_SCH.END_DATE_MONTH > #{schedule.month}) ) AND T_EXISTS_SCH.SCHM_SEQ = TSS.SCHM_SEQ AND T_EXISTS_SCH.SCH_SEQ = TSS.SCH_SEQ) AND EXISTS(SELECT 1 FROM T_SC_SCH_USER TSSU INNER JOIN T_SC_CAL TSC ON TSS.CAL_SEQ = TSC.CAL_SEQ AND (TSSU.CDE_SEQ = #{user.userCompSeq} AND TSSU.CDE_TYPE = 'C' OR TSSU.CDE_SEQ = #{user.userDeptSeq} AND TSSU.CDE_TYPE = 'D' OR TSSU.CDE_SEQ = #{user.userSeq} AND TSSU.CDE_TYPE = 'I') AND TSC.CAL_SEQ IN #{calendar.unAuthorizedCalendarSequence}
AND TSSU.USE_YND = 'Y' AND TSC.USE_YND = 'Y' WHERE 1 = 1 AND TSSU.SCHM_SEQ = TSS.SCHM_SEQ AND TSSU.SCH_SEQ = TSS.SCH_SEQ) AND TSS.USE_YND = 'Y'
